import React, { Component } from 'react';
import './App.css';
import axios from 'axios'
import PlayerCard from './components/PlayerCard';

class App extends Component{
  constructor() {
    super();
    this.state ={
      players: []
    }

    console.log('constructor render') //lifecycle -- constructor
  }

  componentDidMount() {
    axios.get('http://localhost:5000/api/players')
    .then(res => {
      console.log(res.data)
      this.setState({players: res.data})
      console.log('current state: ',this.state.players) // making sure that setState logs
      console.log('componentDidMount render')  //lifecycle -- componentDidMount
    })
  }
  
render() {
  console.log('render() render')  //lifecycle -- render()
  return (
    <div>
      <PlayerCard/>
    </div>
  )
}
}


export default App;
